resources.string:
val (text, button) = createRefs()\n\n

Text(\n
    text = "This is a text",
    modifier = Modifier.constrainAs(text) {
        linkTo(
            parent.start,
            parent.end,
            bias = 0.3F
        )
    }
)

Button(
    modifier = Modifier.constrainAs(button) {
        bottom.linkTo(parent.bottom)
        start.linkTo(parent.start)
    }
    onClick = { }
) { Text("Hello World" )
